const user = {
  id: 1124,
  name: 'Morgrace Precious',
  image: 'https://i.pravatar.cc/48?u=1124',
};
const imageSlide = [
  '/public/img/trophy-1.jpg',
  '/public/img/trophy-2.jpg',
  '/public/img/trophy-3.jpg',
];
const Header = function () {
  return (
    <header className="grid grid-cols-2 gap-8">
      <UserAccount />
      <DiamondToken />
      <Carousel />
    </header>
  );
};
const UserAccount = function () {
  return (
    <div className="flex gap-1 items-center">
      <img
        className="rounded-[50%] w-[3rem]"
        src={`${user['image']}`}
        alt="profile pix"
      />
      <div className="grid">
        <span className="font-bold">{user['name']}</span>
        <span>
          <svg className="">
            <use href="/img/icomoon/sprite#icon-diamond"></use>
          </svg>
          ID-{user['id']}
        </span>
      </div>
    </div>
  );
};
const DiamondToken = function () {
  return (
    <div className="bg-[#4EA4FF] font-semibold justify-self-end !p-[.1rem_1rem]">
      160
    </div>
  );
};
const Carousel = function () {
  return (
    <div className=" rounded-[5px] w-full h-[20vh] bg-cover bg-center bg-no-repeat bg-(image:--bg-header) !p-[1.5rem_2.5rem] grid col-span-2">
      <div className="text-[var(--offwhite)] w-[70%] grid">
        <h1 className="text-[1.4rem] font-semibold">
          Test Your Knowledge with Quizzes
        </h1>
        <p className="font-semibold text-[0.6rem]">
          You're just looking for a playful way to learn <br />
          new facts, our quizzes are designed to <br /> entertain and educate.
        </p>
        <button className="font-semibold text-[1rem] text-[#094298] justify-self-start bg-[#ffffff] rounded-[3px] !p-[.1rem_1rem] ">
          Play Now
        </button>
      </div>
    </div>
  );
};
export default Header;
